import Vue from 'vue'//导入Vue库中的vue对象
import VueRouter from 'vue-router'//导入路由库中的路由对象
//前面是设置的别名
Vue.use(VueRouter)//导入插件，必须通过Vue使用(use())后才能生效

const routes = [
  {
    path: '/',
    name: 'home',
    component: HomeView
  }
]

const router = new VueRouter({
  routes
})
//导出创建好的路由对象
export default router
