import Vue from 'vue'//导入Vue库中的vue对象
import VueRouter from 'vue-router'//导入路由库中的路由对象
import Home from '@/views/Home.vue'
import Login from '@/views/Login.vue'
import Register from '@/views/Register.vue'
import Consulting from '@/views/Consulting.vue'
import Shop from '@/views/Shop.vue'
import Search from '@/views/Search.vue'
//前面是设置的别名
Vue.use(VueRouter)//导入插件，必须通过Vue使用(use())后才能生效

const routes = [
  //路由对象，一个路由对象配置一个组件界面的信息
  //path，请求组件界面路径
  //name，请求组件定义名字
  //component，访问的是哪个组件
  {
    path: '/',
    name: 'home',
    component: Home
  },
  {
    path: '/login',
    name: 'login',
    component: Login
  },
  {
    path: '/register',
    name: 'register',
    component: Register
  },
  {
    path: '/consulting',
    name: 'consulting',
    component: Consulting
  },
  {
    path: '/shop',
    name: 'shop',
    component: Shop
  },
  {
    path: '/search',
    name: 'search',
    component: Search
  }
]
//创建路由对象，需要将上面配置的路由信息(数组)
const router = new VueRouter({
  mode: 'hash',
  routes
})
//导出创建好的路由对象
export default router
